import { Injectable, NgZone } from '@angular/core';
import { ElectronService } from 'ngx-electron';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Subject, BehaviorSubject } from 'rxjs';
import { BaseService } from './base.service';
import { MatBottomSheetRef, MatBottomSheet } from '@angular/material/bottom-sheet';
import { RenameSheetComponent } from '../rename-sheet/rename-sheet.component';


@Injectable()
export class DriveService {

  googleLoginEvent = new Subject();
  googleLogoutEvent = new Subject();
  fetchDriveFilesEvent = new Subject();
  previewEmitter = new Subject();
  renameEmitter = new Subject();
  deleteEmitter = new Subject();

  public errorImg = 'data:image/png;base64,';

  // tslint:disable-next-line: variable-name
  constructor(private _electronService: ElectronService, private baseService: BaseService, private _bottomSheet: MatBottomSheet) { }

  checkGoogleLogin() {
    const loggedIn = this._electronService.ipcRenderer.sendSync('check-google-login');
    if (loggedIn) {
      return true;
    } else {
      return false;
    }
  }

  googleLogin() {
    this._electronService.ipcRenderer.send('google-drive-login');
    this._electronService.ipcRenderer.once('google-login-response', (event, success) => {
      if (success) {
        this.googleLoginEvent.next(true);
        console.log('sent login event')
        // this.googleLoginEvent.complete();
      } else {
       this.baseService.showSnackBar('Error in Google Login', 'failure')
      }
    });

  }

  fetchDriveFiles() {
    this._electronService.ipcRenderer.send('fetch-drive-files');
    this._electronService.ipcRenderer.once('fetch-drive-files-response-success', (event, files) => {
      console.log(files);
      this.fetchDriveFilesEvent.next(files.data.files);
      // this.fetchDriveFilesEvent.complete();
    });

    this._electronService.ipcRenderer.once('fetch-drive-files-response-failure', (event) => {
      this.baseService.showSnackBar('Error in Fetching Files', 'failure')
      
    });
  }

  getRandomKey() {
    return ( '_' + Math.random().toString(36).substr(2, 15));
  }

  fetchPreview(url) {
    const uniqueChannel = `return-preview-${this.getRandomKey()}`;
    console.log('sending event')
    this._electronService.ipcRenderer.send('fetch-drive-preview', url, uniqueChannel);

    this._electronService.ipcRenderer.once(uniqueChannel, (event, image) => {
      this.previewEmitter.next("data:image/png;base64," + image);
      this.previewEmitter.complete();
    })
  }

  openDriveFile(url) {
    this._electronService.ipcRenderer.send('open-drive-file', url);
  }

  renameFile(file) {
    let bottomSheetRef: MatBottomSheetRef;
    let extension: string;

    if(file.mimeType === "application/vnd.google-apps.document") {
      bottomSheetRef = this._bottomSheet.open(RenameSheetComponent);
    } else {
      let i = file.name.lastIndexOf('.')+1
      extension = file.name.slice(i);
      bottomSheetRef = this._bottomSheet.open(RenameSheetComponent, {data: extension});
    }

    bottomSheetRef.afterDismissed().subscribe(data => {
      if(data) {
        file.mimeType === "application/vnd.google-apps.document" ? this.renameFileRequest(file.id, file.name ,data) : this.renameFileRequest(file.id, file.name ,`${data}.${extension}`);
      }
    });
  }

  renameFileRequest(id, oldname, newName) {
    this._electronService.ipcRenderer.send('rename-drive-file', id, newName);
    this._electronService.ipcRenderer.once('rename-drive-file-success', (event, response) => {

    this.baseService.showSnackBar('File Renamed Successfully', 'success')
    this.renameEmitter.next(newName);
    });

    this._electronService.ipcRenderer.once('rename-drive-file-failure', (event, response) => {
      this.baseService.showSnackBar('Error in Renaming File', 'failure')
      this.renameEmitter.next(oldname);
    });
  }

  delete(fileID) {
    this._electronService.ipcRenderer.send('delete-drive-file', fileID);
    this._electronService.ipcRenderer.once('delete-drive-file-success', (event, response) => {

    this.baseService.showSnackBar('File Deleted Successfully', 'success')
    this.deleteEmitter.next(true);
    });

    this._electronService.ipcRenderer.once('delete-drive-file-failure', (event, response) => {
      this.baseService.showSnackBar('Error in Deleteing File', 'failure')
      this.deleteEmitter.next(false);
    });
  }

  downloadFile(exportLink, name, mimeType, downloadAs) {
    this._electronService.ipcRenderer.send('download-drive-file', exportLink,name, mimeType, downloadAs);
  }

  googleLogout() {
    this._electronService.ipcRenderer.send('google-logout');

    this._electronService.ipcRenderer.once('google-logout-response', (event, success) => {
      if(success) {
        this.baseService.showSnackBar('Logout Successful', 'success')
        this.googleLogoutEvent.next();
      } else {
        this.baseService.showSnackBar('Logout Failed', 'failure')
        // this.googleLogoutEvent.next(false);
      }
    })
  }


}
